COMMANDS.COMMAND:
import com.applang.berichtsheft.plugin.*;
BerichtsheftPlugin.setProperty("ADB_OPERATION", oper);
BerichtsheftPlugin.setProperty("ANDROID_FILE", androidFile);
BerichtsheftPlugin.setProperty("PC_FILE", pcFile);

buf = new StringBuffer();
if (androidFile.length() > 0 && pcFile.length() > 0) {
	cmd = com.applang.components.AndroidBridge.buildAdbCommand(oper, androidFile, pcFile);
	if (params.length() > 0)
		cmd += " "+params;
	buf.append(cmd);
}
buf.toString();
